import {Component} from "@angular/core";
import {FormControl, FormGroup, Validators} from "@angular/forms";
import {FormService} from "../../services/form.service";

@Component({
  selector: 'app-modal',
  templateUrl: './modal.component.html'
})

export class ModalComponent {

  constructor(public formService: FormService, ) {
  }

  formGroup = new FormGroup({
    name: new FormControl('', [Validators.required]),
    phone: new FormControl('', [Validators.required, Validators.pattern(/^\d{11}$/)]),
    email: new FormControl('', [Validators.required, Validators.email])
  })

  submit(): void {
    console.log(1342);
    this.formService.sendRequest(this.formGroup.getRawValue()).subscribe(
      (response) => {
        console.log('API response:', response);
      },
      (error) => {
        console.error('Error:', error);
      }
    );
  }
}
<div class="bg-container-bg-color p-5 md:w-[50vw] mx-auto z-[9999999] max-w-3xl">
  <div class="bg-container-bg-color rounded-2xl">
    <!--  title-->
    <div class="flex justify-between items-center mb-5">
      <div>
        <h2 class="text-lg w-[75%] md:w-auto font-semibold">Fill out the form to Get Started</h2>
      </div>
      <div>
        <button matDialogClose="">
          <img src="assets/images/closesquare.svg" />
        </button>
      </div>
    </div>

    <!--  content-->
    <form [formGroup]="formGroup" class="flex flex-col gap-5">
      <div class="mb-3">
        <input [formControl]="formGroup.controls.name"  class="w-full bg-input-bg border-input-border border-opacity-40 rounded-xl px-4 py-2 border" id="first-name" type="text"  placeholder="Name">
        <div *ngIf="formGroup.get('name')?.hasError('required') && formGroup.get('name')?.touched">
          Name is required.
        </div>
      </div>

      <div class="mb-3">
        <input [formControl]="formGroup.controls.phone"  class="w-full bg-input-bg border-input-border border-opacity-40 rounded-xl px-4 py-2 border" id="phone" type="text" placeholder="Phone number">
        <div *ngIf="formGroup.get('phone')?.hasError('required') && formGroup.get('phone')?.touched">
          Phone is required.
        </div>
        <div *ngIf="formGroup.get('phone')?.hasError('pattern') && formGroup.get('phone')?.touched">
          Please enter a 10-digit number.
        </div>
      </div>

      <div class="mb-3">
        <input [formControl]="formGroup.controls.email"  class="w-full bg-input-bg border-input-border border-opacity-40 rounded-xl px-4 py-2 border" id="email" type="text" placeholder="Email address">
        <div *ngIf="formGroup.get('email')?.hasError('required') && formGroup.get('email')?.touched">
          Email is required.
        </div>
        <div *ngIf="formGroup.get('email')?.hasError('email') && formGroup.get('email')?.touched">
          Please enter a valid email address.
        </div>
      </div>

      <button class="w-full bg-button-color rounded-xl p-2" (click)="submit()" [disabled]="formGroup.invalid"  matDialogClose="">
        Submit
      </button>
    </form>
  </div>
</div>
