import {Component, ElementRef, OnInit} from "@angular/core";
import {MatDialog} from "@angular/material/dialog";
import {ModalComponent} from "../../shared/modals/modal.component";
import {animate, style, transition, trigger} from "@angular/animations";
import {ScrollService} from "../../services/scroll.service";

@Component({
  selector: 'app-hero',
  templateUrl: './hero.component.html',
  animations: [
  ],
})

export class HeroComponent implements OnInit{
  showImage = false;
  playStarted = false
  constructor(public dialog: MatDialog, private scrollService: ScrollService, private el: ElementRef) {}


  scrollToComponent(componentKey: string) {
    this.scrollService.scrollToComponent(componentKey);
  }

  currentIndex = 0;
  images = [
    'assets/images/hero1.svg',
    'assets/images/hero2.svg',
    'assets/images/hero3.svg',
    'assets/images/hero4.svg',
    'assets/images/hero5.svg',
  ];

  openModal() {
    this.dialog.open(ModalComponent);
  }

  ngOnInit() {
    setInterval(() => {
      this.currentIndex = (this.currentIndex + 1) % this.images.length;
    }, 200);

    setTimeout(() => {
      this.showImage = true;
    }, 1);

    setInterval(() => {
      // Update the progress to a new value between 0 and 100
      const newProgress = Math.random() * 100;
      this.updateProgress(newProgress);
    }, 1000);
  }

  playButton(): void {
    this.playStarted = true
  }

  currentProgress: number = 0; // Initialize currentProgress to 0

  // Function to update the progress
  updateProgress(newProgress: number) {
    this.currentProgress = newProgress;
  }
}

<div class="flex items-center justify-center ">
  <div class="mb-16 md:mb-40 md:mt-40 mt-40">

    <div class="flex flex-col text-center">
      <div class="flex justify-center mb-5 md:mb-10">
        <img
          [src]="images[currentIndex]"
          class="slideshow-image w-48 md:w-auto"
        />
      </div>
      <div class="flex flex-col items-center mb-5 md:mb-10">
        <div class="hidden md:block">
          <div class="flex flex-col items-center md:mb-10 text-xl md:text-5xl ">
            <h1 class="font-bold ">Робот для Увеличения Продаж и </h1>
            <h1 class="font-bold md:w-full">Удержания Клиентов для B2C</h1>
            <h1 class="font-bold md:w-full">Компаний</h1>
          </div>
        </div>

        <div class="md:hidden">
          <h1 class="font-bold mb-4 md:mb-10 md:w-[85%] text-[32px]">
            Создадим для вас Голосовых/ Текстовых Роботов, которые могут коммуницировать с вашими клиентами неотличимо от реальных людей.
          </h1>
        </div>


        <p class="w-full md:w-[50%] text-[16px]">Создадим для вас Голосовых/ Текстовых Роботов, которые могут коммуницировать с вашими клиентами неотличимо от реальных людей.</p>
      </div>
      <div class="flex flex-col md:flex-row items-center md:justify-center space-y-3 md:space-y-0 md:space-x-5">
        <button (click)="openModal()" class="z-[50] font-semibold bg-button-color bg-opacity-70 p-4 rounded-3xl px-8 md:w-[25%]">Получить консультацию</button>
        <button  (click)="scrollToComponent('component6')"  class="z-[50] font-semibold bg-container-bg-color bg-opacity-50 p-4 rounded-3xl px-8 md:w-[25%]">Как это работает?</button>
      </div>
    </div>
  </div>
</div>
