import {Component} from "@angular/core";
import {ModalComponent} from "../../shared/modals/modal.component";
import {MatDialog} from "@angular/material/dialog";

@Component({
  selector: 'app-cases',
  templateUrl: './cases.component.html'
})

export class CasesComponent {

  constructor(private dialog: MatDialog) {
  }

  cases = [
    {name: 'Ритейл', active: true},
  ]
  containers = [
    {
      header: '+200%',
      description: 'Покупок клиентов',
      text: 'Омни-канальная автоматизированная воронка продаж утраивает количество покупок клиентов.'
    },
    {
      header: '+500%',
      description: 'Реактивированных клиентов',
      text: 'Человекоподобные роботы увеличивают количество реактивированных клиентов в 6 раз.'
    },
    {
      header: '60%',
      description: 'Автоматизация',
      text: 'Автоматизация 60% процессов по возвращению клиентов: звонки, отправка SMS, сообщений в мессенджерах.'
    }
  ]

  openModal() {
    this.dialog.open(ModalComponent)
  }
}
<div class="mb-16 md:mb-40">
  <h2 class="font-bold text-2xl mb-4 md:mb-5">Кейсы</h2>
  <ul class="flex flex-wrap gap-2 md:justify-between mb-3">
    <li *ngFor="let case of cases" class="font-semibold mb-2 md:mb-0 md:mr-2">
      <span [ngClass]="case.active ? 'text-active-tab-color' : '' " >{{case.name}}</span>
    </li>
  </ul>
  <div class="flex flex-col md:flex-row gap-6">
    <div class="bg-container-bg-color bg-opacity-50 w-full md:w-1/3 md:mb-6 rounded-xl p-4" *ngFor="let container of containers" >
      <h2 class="font-bold text-lg md:text-xl mb-2 md:mb-3">{{container.header}}</h2>
      <p class="text-active-tab-color opacity-70 mb-2 md:mb-3">{{container.description}}</p>
      <p>{{container.text}}</p>
    </div>
  </div>

  <div class="bg-container-bg-color bg-opacity-50 rounded-xl p-4 flex flex-col md:flex-row justify-between items-center">
    <div class="mb-3 md:mb-0">
      <h2 class="font-bold text-xl md:text-2xl mb-2 md:mb-3">
        Хотите узнать больше о наших кейсах?
      </h2>
      <h1 class="font-bold text-3xl md:text-4xl text-active-tab-color">
        Получите бесплатную консультацию
      </h1>
    </div>

    <div class="w-[100%] md:w-[30%]">
      <button (click)="openModal()"  class="font-semibold bg-button-color bg-opacity-70 p-3 md:p-4 rounded-3xl px-6 w-[100%] md:w-[100%]">
        Получить консультацию
      </button>
    </div>
  </div>
</div>
