import {Component, EventEmitter, Input, Output} from "@angular/core";
import {Router} from "@angular/router";
import {MatDialogRef} from "@angular/material/dialog";
import {ScrollService} from "../../services/scroll.service";

@Component({
  selector: 'app-sidebar',
  templateUrl: './sidebar.component.html'
})

export class SidebarComponent {
  @Input() isSidebarOpen!: boolean;
  @Output() toggle = new EventEmitter<void>();

  constructor(private scrollService: ScrollService) {}

  scrollToComponent(componentKey: string) {
    this.scrollService.scrollToComponent(componentKey);
  }
  toggleSidebar() {
    this.isSidebarOpen = !this.isSidebarOpen;
    this.toggle.emit();
  }

  navbar =  [
    {name: "Application Areas", link: 'component1', url: ''},
    {name: "Key Benefits", link: 'component2', url: ''},
    {name: "Getting Started", link: 'component3', url: ''},
    {name: "F.A.Q.", link: 'component4', url: ''},
    {name: "Blog", link: 'blog', url: 'blog'},
  ]

  close(link: string) {
    this.isSidebarOpen = false
    this.scrollToComponent(link)
  }

}
<div class="bg-bg relative z-[9999999]">
  <div [class.translate-x-0]="isSidebarOpen" [class.translate-x-full]="!isSidebarOpen"
       class="fixed inset-y-0 right-0 w-[100%] bg-bg text-white transform transition-transform ease-in-out duration-300">
    <div class="flex justify-between items-center p-5">
      <div>
        <p>Menu</p>
      </div>
      <div>
        <img (click)="toggleSidebar()" src="assets/images/closesquare.svg" />
      </div>
    </div>
    <div class="text-center mt-10">
      <ul>
        <li *ngFor="let link of navbar" class="text-xl mb-5">
          <button (click)="close(link.link)"  routerLink="/{{link.url}}" >{{link.name}}</button>
        </li>
      </ul>
    </div>
  </div>

</div>
