import {Component, ElementRef, OnInit} from "@angular/core";
import {MatDialog} from "@angular/material/dialog";
import {ModalComponent} from "../../shared/modals/modal.component";
import {Router} from "@angular/router";
import {ScrollService} from "../../services/scroll.service";

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
})

export class HeaderComponent{

  constructor(private scrollService: ScrollService, public dialog: MatDialog) {}

  scrollToComponent(componentKey: string) {
    this.scrollService.scrollToComponent(componentKey);
  }

  scrollToTop() {
    window.scrollTo({top: 0, behavior: 'smooth'})
    this.dialog.closeAll();
  }

  navbar =  [
    {name: "Как можно применить?", link: 'component1', url: ''},
    {name: "Наши преимущества", link: 'component2', url: ''},
    {name: "Этапы работы с нами", link: 'component3', url: ''},
    {name: "F.A.Q.", link: 'component4', url: ''},
    {name: "Блог", link: 'blog', url: 'blog'},
  ]

  isSidebarOpen = false;

  toggleSidebar() {
    this.isSidebarOpen = !this.isSidebarOpen;
  }

}
<div class="sticky top-0 left-0 right-0 bg-bg h-[10vh] z-[9999]">
  <div >
    <div class="mb-16 px-4 md:px-16 flex md:flex-row items-center  justify-between">
      <div class="mb-4 md:mb-0 flex items-center relative z-[49]">
        <img src="assets/images/logo.svg" class="w-full md:w-auto" routerLink="" />
        <div class="flex font-semibold cursor-pointer text-[20px]" routerLink="" (click)="scrollToTop()" >
          <span class="hidden md:flex">StellarTech</span>
          <span class="hidden md:flex text-active-tab-color">.ai</span>
        </div>
      </div>
      <div class="md:flex md:flex-row hidden">
        <ul class="flex space-x-6">
          <li *ngFor="let nav of navbar" class="text-[14px]">
            <div routerLink="{{nav.url}}" class="cursor-pointer relative z-[49]" (click)="scrollToComponent(nav.link)" >
              {{ nav.name }}
            </div>
          </li>
        </ul>
      </div>
      <div class="flex gap-2 md:gap-4 items-center">
        <div class="bg-black">
          <a href="mailto:info@stellartech.ai" class="bg-container-bg-color bg-opacity-50 py-1.5 px-3 rounded-xl text-[16px]">info@stellartech.ai</a>
        </div>
      </div>
      <div class="md:hidden">
        <button class="z-[99]">
          <mat-icon (click)="toggleSidebar()">menu</mat-icon>
        </button>
      </div>
    </div>
  </div>
  <app-sidebar [isSidebarOpen]="isSidebarOpen" (toggle)="toggleSidebar()"></app-sidebar>

</div>
