        <div *ngFor="let container of containers" class="bg-container-bg-color bg-opacity-50 rounded-xl p-4 flex">
          <div class="flex items-start ">
            <img class="w-8 md:w-[40px] mt-3 mr-3" src="assets/images/messagequestion.svg"/> <!-- Adjust image width for mobile and larger screens -->
            <div>
              <h2 class="font-bold text-lg">{{container.title}}</h2>
              <p class="text-[16px]">{{container.text}}</p>
