import {AfterViewInit, Component, ElementRef, OnInit, ViewChild} from "@angular/core";
import {ScrollService} from "../../services/scroll.service";
import {MatDialog} from "@angular/material/dialog";
import {ModalComponent} from "../../shared/modals/modal.component";

@Component({
  selector: 'app-solutions',
  templateUrl: './solutions.component.html'
})

export class SolutionsComponent implements OnInit {
  constructor(private scrollService: ScrollService, private el: ElementRef, private dialog: MatDialog) {}

  openModal() {
    this.dialog.open(ModalComponent)
  }

  ngOnInit() {
    this.scrollService.setScrollFunction('component1', () => this.scrollToComponent());
  }

  scrollToComponent() {
    this.el.nativeElement.scrollIntoView({ behavior: 'smooth', block : 'center'});
  }

  leftSolutions = [
    {
      image: 'assets/images/solutions1.svg',
      text: 'Контроль Качества и NPS'
    },
    {
      image: 'assets/images/solutions2.svg',
      text: 'Вовлечение по Программе Лояльности'
    },
    {
      image: 'assets/images/solutions3.svg',
      text: 'Напоминание о Брошенной Корзине'
    },
    {
      image: 'assets/images/solutions4.svg',
      text: 'Поздравления с днем Рождения'
    }
  ]
  rightSolutios = [
    {
      image: 'assets/images/solutions6.svg',
      text: 'Информирование об акциях/ распродажах'
    },
    {
      image: 'assets/images/application1.svg',
      text: 'Обработка Входящих Звонков/ Заявок'
    },
    {
      image: 'assets/images/application2.svg',
      text: 'Предупреждения о Сгорающих Бонусах'
    },
    {
      image: 'assets/images/solutions8.svg',
      text: 'Звонок по Выезду Курьера/ о Доставленном Заказе'
    }
  ]
}

<img src="assets/images/application_bg.svg" class="absolute text-center z-[40] top-[65vh] left-0 h-[110%] "  #applicationAreas/>
<div class="hidden md:block md:mb-40 relative z-[41]">
  <div class="flex flex-col md:flex-row gap-5">
    <div class="flex flex-col justify-center w-full md:w-1/3 mb-6 md:mb-0 items-center">
      <img src="assets/images/Group%2096.svg" class="w-2/3 mb-3" />
      <h2 class="font-bold mb-3 ">Узнать больше</h2>
      <div>
        <button class="font-semibold bg-button-color bg-opacity-70 p-4 rounded-3xl px-8 " (click)="openModal()" >Получить консультацию</button>
      </div>
    </div>
    <div>
      <h2 class="font-bold text-[32px] mb-10">Где чаще всего используют?</h2>
      <div class="flex flex-col md:flex-row gap-2">
        <ul class="flex flex-col w-full md:w-1/2">
          <li *ngFor="let sol of leftSolutions" class="flex mb-5">
            <div class="mr-3 w-10 h-10 md:w-12 md:h-12 flex-shrink-0">
              <img src="{{sol.image}}" />
            </div>
            <p>{{sol.text}}</p>
          </li>
        </ul>

        <ul class="flex flex-col w-full md:w-1/2">
          <li *ngFor="let sol of rightSolutios" class="flex mb-5">
            <div class="mr-3 w-10 h-10 md:w-12 md:h-12 flex-shrink-0">
              <img src="{{sol.image}}" class="" />
            </div>
            <p>{{sol.text}}</p>
          </li>
        </ul>
      </div>
    </div>
  </div>
</div>
